import React, { useState } from "react";

import NewExpense from "./components/Expenses/NewExpense";
import Expenses from "./components/Expenses/Expenses";

const DUMMY_EXPENSES = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

const App = () => {
  const [filteredExpenses, setFilteredExpenses] = useState(DUMMY_EXPENSES);
  const filterChangeHandler = (selectedYear) => {
    setFilteredExpenses(
      expenses.filter((expense) => expense.date.getFullYear() == selectedYear)
    );
  };
  const [expenses, setExpenses] = useState(filteredExpenses);
  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => [expense, ...prevExpenses]);
  };
  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      <Expenses items={filteredExpenses} onFilterChange={filterChangeHandler} />
    </div>
  );
};

export default App;
